INSERT INTO PRODUCTS (ID, NAME, EAN, CATEGORY, IMAGE_NAME, DESCRIPTION, VERSION) VALUES (-1, 'RadiantGlow Serum', '1234567890123', 'BEAUTY', 'beauty_1.jpg', 'Achieve a luminous complexion with RadiantGlow Serum. Infused with natural antioxidants and hyaluronic acid, this serum deeply hydrates and revitalizes your skin, leaving it smooth, supple, and radiant.', 1);
