INSERT INTO PRODUCTS (ID, NAME, EAN, CATEGORY, IMAGE_NAME, DESCRIPTION, VERSION) VALUES (-28, 'ZenHaven Bedframe', '3456789012345', 'FURNITURE', 'furniture_3.jpg', 'Create a serene bedroom retreat with the ZenHaven Bedframe. Crafted from sustainable bamboo, this minimalist bedframe offers clean lines and a calming presence. Its low-profile design and earthy tones contribute to a harmonious sleeping environment.', 1);
