INSERT INTO PRODUCTS (ID, NAME, EAN, CATEGORY, IMAGE_NAME, DESCRIPTION, VERSION) VALUES (-36, 'TurboBlend Pro 5000', '9781234567001', 'HOME_APPLIANCES', 'ha_1.jpg', 'The TurboBlend Pro 5000 is a high-performance blender designed to effortlessly blend smoothies, soups, and more. With its powerful motor and advanced blade technology, you can achieve smooth and consistent results every time.  Product Name: SteamMist Deluxe Iron', 1);
