INSERT INTO PRODUCTS (ID, NAME, EAN, CATEGORY, IMAGE_NAME, DESCRIPTION, VERSION) VALUES (-41, 'Sparkling Solitaire Necklace', '1234567890123', 'JEWELRY', 'jewelry_1.jpg', 'Elevate your elegance with our Sparkling Solitaire Necklace. A dazzling round-cut diamond is delicately suspended on a fine white gold chain, creating a timeless piece that adds sophistication to any outfit.', 1);
