INSERT INTO PRODUCTS (ID, NAME, EAN, CATEGORY, IMAGE_NAME, DESCRIPTION, VERSION) VALUES (-43, 'Regal Pearl Tiara', '3456789012345', 'JEWELRY', 'jewelry_3.jpg', 'Embody royalty with our Regal Pearl Tiara. Handcrafted with lustrous freshwater pearls and sparkling crystals, this tiara exudes elegance and charm, making it the perfect accessory for weddings and special occasions.', 1);
INSERT INTO PRODUCTS (ID, NAME, EAN, CATEGORY, IMAGE_NAME, DESCRIPTION, VERSION) VALUES (-44, 'Celestial Constellation Bracelet', '4567890123456', 'JEWELRY', 'jewelry_4.jpg', 'Find your place in the cosmos with our Celestial Constellation Bracelet. Sterling silver links are adorned with intricately crafted zodiac constellations, creating a celestial piece that reflects the mysteries of the night sky.', 1);
