INSERT INTO PRODUCTS (ID, NAME, EAN, CATEGORY, IMAGE_NAME, DESCRIPTION, VERSION) VALUES (-46, 'PowerGrip Pro Fitness Gloves', '1234567890123', 'SPORTS', 'sports_1.jpg', 'Enhance your weightlifting and fitness routines with the PowerGrip Pro Fitness Gloves. These gloves provide superior grip and comfort, allowing you to lift with confidence and push your limits.', 1);
