INSERT INTO PRODUCTS (ID, NAME, EAN, CATEGORY, IMAGE_NAME, DESCRIPTION, VERSION) VALUES (-48, 'AquaFlex Pro Swim Goggles', '3456789012345', 'SPORTS', 'sports_3.jpg', 'Dive into the pool with the AquaFlex Pro Swim Goggles. Designed for serious swimmers, these goggles offer a comfortable fit, anti-fog lenses, and a sleek hydrodynamic profile to help you glide through the water effortlessly.', 1);
INSERT INTO PRODUCTS (ID, NAME, EAN, CATEGORY, IMAGE_NAME, DESCRIPTION, VERSION) VALUES (-49, 'TurboDrive 5000 Indoor Cycling Bike', '4567890123456', 'SPORTS', 'sports_4.jpg', 'Experience the thrill of indoor cycling with the TurboDrive 5000. This state-of-the-art stationary bike offers customizable resistance levels, a digital console to track your workouts, and a comfortable saddle for long training sessions.', 1);
INSERT INTO PRODUCTS (ID, NAME, EAN, CATEGORY, IMAGE_NAME, DESCRIPTION, VERSION) VALUES (-50, 'StrikeZone Precision Soccer Ball', '5678901234567', 'SPORTS', 'sports_5.jpg', 'Elevate your soccer skills with the StrikeZone Precision Soccer Ball. Engineered for accuracy and control, this ball features strategically placed panels for enhanced ball manipulation, making it ideal for training and improving your game.', 1);
INSERT INTO PRODUCTS (ID, NAME, EAN, CATEGORY, IMAGE_NAME, DESCRIPTION, VERSION) VALUES (-51, 'FlutterPals', '1234567890123', 'TOYS', 'toys_1.jpg', 'FlutterPals are adorable interactive plush toys designed to mimic the movements and sounds of real birds. These soft and huggable companions chirp, tweet, and flap their wings, delighting children with their lifelike behavior.', 1);
INSERT INTO PRODUCTS (ID, NAME, EAN, CATEGORY, IMAGE_NAME, DESCRIPTION, VERSION) VALUES (-52, 'RoboBuilder Set', '2345678901234', 'TOYS', 'toys_2.jpg', 'The RoboBuilder Set is an advanced building kit that allows kids to construct and program their own robots. With modular components and a user-friendly coding interface, children can create robots that walk, dance, and even perform tasks using sensors and motors.', 1);
INSERT INTO PRODUCTS (ID, NAME, EAN, CATEGORY, IMAGE_NAME, DESCRIPTION, VERSION) VALUES (-53, 'AquaAdventures Playset', '3456789012345', 'TOYS', 'toys_3.jpg', 'Dive into imaginative underwater fun with the AquaAdventures Playset! This interactive water playset features a cascading waterfall, spinning whirlpool, and floating animal figurines. Kids can explore aquatic adventures and develop fine motor skills through water-based play.', 1);
INSERT INTO PRODUCTS (ID, NAME, EAN, CATEGORY, IMAGE_NAME, DESCRIPTION, VERSION) VALUES (-54, 'Galactic Explorers Spaceship', '4567890123456', 'TOYS', 'toys_4.jpg', 'Blast off into the cosmos with the Galactic Explorers Spaceship! This futuristic playset includes a highly detailed spaceship, poseable astronaut figures, and detachable space vehicles. With flashing lights, sound effects, and a retractable landing gear, kids can embark on interstellar missions.', 1);
INSERT INTO PRODUCTS (ID, NAME, EAN, CATEGORY, IMAGE_NAME, DESCRIPTION, VERSION) VALUES (-55, 'PuzzleBots Challenge', '5678901234567', 'TOYS', 'toys_5.jpg', 'Engage young minds with the PuzzleBots Challenge, a brain-teasing robotic puzzle game. Players must program their mini robots to navigate through mazes and obstacles to reach the target. The set includes a variety of challenge cards, encouraging critical thinking and coding skills.', 1);
